let token = null;

window.addEventListener('load', () => {
  if (!window.localStorage.getItem('token')) {
    window.location.href = './login.html';
  } else {
    token = window.localStorage.getItem('token');
  }
  getAllItems();
});

document.querySelector('#createItemButton').addEventListener('click', () => {
  createItem();
});

function createItem() {
  let title = document.querySelector('#createItem').value;
  if (!title) {
    console.log('cant create empty title');
    return;
  }

  fetch('http://localhost:3000/api/toDoItem', {
    method: 'POST',
    headers: {
      Accept: 'application/json',
      'Content-Type': 'application/json',
      'x-auth': token,
    },
    body: JSON.stringify({
      title,
    }),
  })
    .then(res => res.json())
    .then(data => {
      getAllItems();
    })
    .catch(err => console.log(err));
}

function getAllItems() {
  fetch('http://localhost:3000/api/toDoItem', {
    method: 'GET',
    headers: {
      Accept: 'application/json',
      'Content-Type': 'application/json',
      'x-auth': token,
    },
  })
    .then(res => res.json())
    .then(data => {
      createCard(data);
    })
    .catch(err => console.log(err));
}

function createCard(data) {
  document.querySelector('#list').innerHTML = '';

  let ul = document.createElement('ul');
  data.forEach(item => {
    let li = document.createElement('li');
    let para = document.createElement('p');

    para.addEventListener('click', () => {
      fetch(`http://localhost:3000/api/toogleItem/${item._id}/`, {
        method: 'PUT',
        headers: {
          Accept: 'application/json',
          'Content-Type': 'application/json',
          'x-auth': token,
        },
      })
        .then(res => res.json())
        .then(data => {
          console.log(data);

          getAllItems();
        })
        .catch(err => console.log(err));
    });

    para.textContent = item.title;
    para.className = 'item1';
    if (item.checked) {
      para.className = 'checkedItem';
    }
    let deleteButton = document.createElement('button');
    deleteButton.type = 'button';
    deleteButton.textContent = 'X';

    deleteButton.addEventListener('click', () => {
      fetch(`http://localhost:3000/api/deleteItemById/${item._id}/`, {
        method: 'DELETE',
        headers: {
          Accept: 'application/json',
          'Content-Type': 'application/json',
          'x-auth': token,
        },
      })
        .then(res => res.json())
        .then(data => {
          getAllItems();
        })
        .catch(err => console.log(err));
    });
    li.appendChild(para);
    li.appendChild(deleteButton);
    ul.appendChild(li);
  });
  document.querySelector('#list').appendChild(ul);
}
